function stickyFunction(){window.pageYOffset>sticky?header.classList.add("sticky"):header.classList.remove("sticky")}function toTopFunction(){document.body.scrollTop>20||document.documentElement.scrollTop>20?document.getElementById("btn-top").style.display="block":document.getElementById("btn-top").style.display="none"}function topFunction(){document.body.scrollTop=0,document.documentElement.scrollTop=0}window.onscroll=function(){stickyFunction(),toTopFunction()};var header=document.getElementById("main-sticky-menu"),sticky=header.offsetTop;